    public static ArrayList<Makanan> getData() {
        ArrayList<Makanan> list = new ArrayList<>();
        for (String[] item : data) {
            Makanan makan = new Makanan();
            makan.setName((item[0]));
            makan.setOrigin(item[1]);
            makan.setPhoto(item[2]);
            makan.setDesciption(item[3]);
            makan.setPrice(item[4]);

            list.add(makan);
        }

        return list;
    }
